test.config:
indexer { mem_limit = 16M }
searchd {
max_filter_values = 500000 max_packet_size = 18M dist_threads = 4 read_timeout = 1 workers = threads }
source src1 { type = mysql
sql_query = SELECT id, group_id, title, mva FROM test_table sql_attr_uint = group_id sql_attr_multi = uint mva from field }
index block1 { source = src1 path = /block1 docinfo = extern min_word_len = 1 }
index rt { type = rt path = /rt docinfo = extern rt_mem_limit = 128k rt_field = title rt_attr_uint = gid }

test.custom_test:
$results = array ();

// regression crash on SphinxQL empty query
global $g_locals, $sd_address, $sd_sphinxql_port;
$sockStr = "$sd_address:$sd_sphinxql_port";
if ($sd_address == "localhost")
	$sockStr = "127.0.0.1:$sd_sphinxql_port";
$sock1 = @mysql_connect (
	$sockStr,
	$g_locals['db-user'],
	$g_locals['db-password'] ,
	true );
if ( $sock1 === false )
	$results[] = "error: can't connect to searchd: " . @mysql_errno ( $sock1 ) . " : " . @mysql_error ( $sock1 );
$results[] = @mysql_query( "", $sock1 );
$results[] = @mysql_error ( $sock1 );
